import styled from "styled-components"

export const NavBar = styled.nav`
    background-color: var(--black);
    position: fixed;
    bottom: 0;
    width: 100%;
    height: 5rem;
    font-weight: 500;

    ul {
        list-style-type: none;
        display: flex;
        flex-direction: row;
        align-items: center;
    }

    @media screen and (min-width: 40rem) {
        width: 18rem;
        height: 100vh;
        left: 0;
        top: 0;
        padding: 2rem;
    }

    ul {
        flex-direction: column;
        align-items: flex-start;
    }
`
